mapper.select:
select

from 


    and buyorder.bo_id = #{boId}


    and buyorder.bo_buyerName like "%"#{srBuyerName}"%"



     and DATE_FORMAT(buyorder.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d')


     and DATE_FORMAT(buyorder.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')


     and DATE_FORMAT(buyorder.create_time,'%Y-%m-%d') = DATE_FORMAT(#{createTime},'%Y-%m-%d')


    and buyorder.bo_state = #{boState}



      and buyorder.bo_isFinished is null 


      and buyorder.bo_isFinished = #{boIsFinished,jdbcType=BOOLEAN}
